mapper.select:
SELECT id, user_id, board_id, title, content, article_comment_number, article_hit_number
FROM articles
WHERE id = #{articleId}

SELECT id, user_id, board_id, title, content, article_comment_number, article_hit_number
FROM articles
WHERE board_id = #{boardId}

mapper.update:
UPDATE articles
SET title   = #{title},
    content = #{content}
WHERE id = #{articleId}
